    selected[index]=selected[index]+value
    if selected[index] > #factions then
        selected[index]=2
        if index == 1 then
            selected[index]=2
        end
    end
    if index == 1 then
        if selected[index] < 2 then
            selected[index]= #factions
        end
    else
        if selected[index] < 2 then
            selected[index]= #factions
        end
    end
    for i=1, totalSections do
        if i ~= index then
            if selected[i] == selected[index] or  selected[i]==1 then
                upDown(index, value)
            end
        end
    end

    writeMenus()
end




