    Wait.frames(shuffleDeck,5)
    resetBags(true)
    closeBuilder()
    if hasSomethingBeenSpawned then
        --Player[colorSpawnedDeck].attachCameraToObject()
        Player[colorSpawnedDeck].print("Deck spawned to "..colorSpawnedDeck,colorSpawnedDeck)
    end
end

function hideBag(_bag)
    _bag.setTable("selectedCardList", {})
    _bag.clearButtons()
end

function shuffleDeck()
    --spawnedItemPosition={positionSelf.x, positionSelf.y+3, positionSelf.z+10}
     zone=spawnObject({
        type              = "ScriptingTrigger",
        position          = spawnedItemPosition,
        scale             = {3,20,3}
    })
    Wait.frames(shuffleDeck2,2)
end

function shuffleDeck2()
    local objects=zone.getObjects()
    for i,obj in ipairs(objects) do
        if obj.shuffle() then
            obj.setLuaScript('BCBtype="CommandAssetDeck"')
        end
    end
    zone.destroy()
end

function addFaction(obj, player_clicker_color, alt)
    if self.getPosition().z > 0 then --team 1
      if player_clicker_color == "Blue" or player_clicker_color == "Teal" then
          return
      end
  else -- team 2
      if player_clicker_color == "Red" or player_clicker_color == "Orange" then
          return
      end
  end

  if player_clicker_color ~= "Red" and player_clicker_color ~= "Orange" and player_clicker_color ~= "Blue" and player_clicker_color ~= "Teal" then
    return
  end
    totalSections=totalSections+1
    if totalSections>maxSections then
        totalSections=maxSections
    end
    selected[totalSections]=1
    upDown(totalSections, 1)
    writeMenus()
end

function removeFaction(obj, player_clicker_color, alt)
    if self.getPosition().z > 0 then --team 1
      if player_clicker_color == "Blue" or player_clicker_color == "Teal" then
          return
      end
  else -- team 2
      if player_clicker_color == "Red" or player_clicker_color == "Orange" then
          return
      end
  end

  if player_clicker_color ~= "Red" and player_clicker_color ~= "Orange" and player_clicker_color ~= "Blue" and player_clicker_color ~= "Teal" then
    return
  end
    totalSections=totalSections-1
    if totalSections<1 then
        totalSections=1
    end
    writeMenus()
end

function cardSelected()
    numberSelectedCards=numberSelectedCards+1
    writeMenus()
end

function cardUnselected()
    numberSelectedCards=numberSelectedCards-1
    writeMenus()
end

