  local nw = (uiWidth-border*2-sep)/2
  local h = streamerUIPrimaryRowPart(ch, border, -uiHeight, nw, 1, round)
  streamerUIPrimaryRowPart(ch, border+nw+sep, -uiHeight, nw, 2, round)
